return $input.all().map(item => {
  const data = item.json;

  // Split and clean
  const emails = (data.email || "")
    .split(",")
    .map(e => e.trim())
    .filter(e => e.length > 0);

  return {
    json: {
      ...data,
      emails  // now an array of valid emails
    }
  };
});
