// Pull structured output once
const out = $('Loop Over Items').first().json.output || {};

// Back-compat shim (if some nodes still send locationType)
out.lookupType = $input.first().json.lookupType;
// --- helpers ---
const slug = (s) => String(s || '')
  .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
  .replace(/ä/g,'ae').replace(/ö/g,'oe').replace(/ü/g,'ue')
  .replace(/Ä/g,'Ae').replace(/Ö/g,'Oe').replace(/Ü/g,'Ue')
  .replace(/ß/g,'ss')
  .toLowerCase()
  .trim()
  .replace(/[^a-z0-9]+/g,'-')
  .replace(/^-+|-+$/g,'');

// Minimal industry → slug map (extend as needed)
const industryMap = {
  'zahnärzte': 'zahnarzt',
  'zahnarzt': 'zahnarzt',
  'dentists': 'zahnarzt',
  'steuerberater': 'steuerberater',
  'anwalt': 'rechtsanwalt',
};
const rawIndustry = String(out.industry || '').toLowerCase().trim();
const categorySlug = industryMap[rawIndustry] || slug(rawIndustry) || 'zahnarzt';

// Resolve location from lookupType
let locationSegment = '';
if (out.lookupType === 'postal_code') {
  locationSegment = String(  $input.first().json.postalCode || '').trim();
} else if (out.lookupType === 'city') {
  locationSegment = slug(out.city);
} else {
  throw new Error('lookupType must be "postal_code" or "city".');
}
if (!locationSegment) throw new Error('Missing location (postalCode or city).');

// Build URLs
const sucheUrl = `https://www.gelbeseiten.de/suche/${categorySlug}/${encodeURIComponent(locationSegment)}`;
// Optional alternate:
// const branchenUrl = `https://www.gelbeseiten.de/branchen/${categorySlug}/${encodeURIComponent(locationSegment)}`;

return [
  {
    json: {
      url: sucheUrl,
      // altUrl: branchenUrl,   // uncomment if you want a fallback candidate
      parts: {
        lookupType: out.lookupType,
        categorySlug,
        locationSegment
      },
      debug: {
        industryInput: out.industry,
        cityInput: out.city || '',
        postalCodeInput: out.postalCode || ''
      }
    }
  }
];

let message = '';
if($prevNode.name === 'Team Leader'){

  message = { 
    text: $input.first().json.userMessage,
    postal_code: $input.first().json.postal_code,
    district: $input.first().json.district
  };
}else if($prevNode.name === 'Message Felix'){
  message = $input.first().json.message;
}

return {
  message: message
}

