// --- 1. Extract raw text from OpenAI "Message a Model" output ---
let raw = "";
try {
  raw = $json.output[0].content[0].text || "";
} catch (err) {
  throw new Error("Could not locate output[0].content[0].text");
}

// --- 2. Normalize / strip markdown fences ---
raw = raw.trim();
raw = raw.replace(/```json/gi, "").replace(/```/g, "").trim();

// --- 3. Extract the first { ... } block (handles stray text) ---
const start = raw.indexOf("{");
const end = raw.lastIndexOf("}");
if (start === -1 || end === -1) {
  throw new Error("No JSON object detected in response.");
}

let jsonStr = raw.slice(start, end + 1);

// --- 4. Parse JSON safely ---
let data;
try {
  data = JSON.parse(jsonStr);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message + "\nRaw:\n" + jsonStr);
}

