try {
  raw = $json.output[0].content[0].text || "";
} catch (err) {
  throw new Error("Could not locate .output[0].content[0].text in incoming data");
}

// --- 2. Trim and remove Markdown fences ---
raw = raw.trim();
raw = raw.replace(/```json/gi, "").replace(/```/g, "").trim();

// --- 3. If wrapped in a string literal → unwrap it ---
if ((raw.startsWith('"') && raw.endsWith('"')) ||
    (raw.startsWith("'") && raw.endsWith("'"))) {
  try {
    raw = JSON.parse(raw);
  } catch (_) {}
}

// --- 4. Extract JSON from any surrounding junk ---
const start = raw.indexOf("{");
const end = raw.lastIndexOf("}");
if (start === -1 || end === -1) {
  throw new Error("Could not find JSON object in model output.");
}

let jsonStr = raw.slice(start, end + 1);

// --- 5. Parse JSON safely ---
let data;
try {
  data = JSON.parse(jsonStr);
} catch (err) {
  throw new Error("JSON parse failed: " + err.message + "\nRaw: " + jsonStr);
}

// --- 6. Validate minimal structure ---
if (!Array.isArray(data.chapters)) {
  throw new Error("Parsed JSON has no chapters array.");
}

// --- 7. Output clean canonical structure ---
return [
  {
    json: {
      book_title: data.book_title || null,
      author: data.author || null,
      overall_confidence: data.overall_confidence ?? null,
      chapters: data.chapters,
      notes: data.notes || null
    }
  }
];

// Expecting the canonicalizer output to be an array with one item
