//
// Extract and parse JSON from the bullet-point “Message A Model” output
//

let raw = "";

// 1. Safely pull the text field from model output
try {
  raw =
    $json.output?.[0]?.content?.[0]?.text ||
    $json.output?.[0]?.content?.[0]?.output_text ||
    $json.data ||
    "";
} catch (e) {
  raw = "";
}

if (!raw) {
  throw new Error("No model output text found.");
}

// 2. Normalize: trim + remove accidental code fences or markdown
raw = raw
  .trim()
  .replace(/^```json/i, "")
  .replace(/^```/, "")
  .replace(/```$/, "")
  .trim();

// 3. If the entire JSON blob is wrapped in quotes, unwrap
if (
  (raw.startsWith('"') && raw.endsWith('"')) ||
  (raw.startsWith("'") && raw.endsWith("'"))
) {
  try {
    raw = JSON.parse(raw);
  } catch (err) {
    // If it fails, continue with raw unchanged
  }
}

// 4. Extract the FIRST { and LAST } — isolating the JSON object
const start = raw.indexOf("{");
const end = raw.lastIndexOf("}");

if (start === -1 || end === -1) {
  throw new Error("JSON object braces not found in the model output.");
}

const jsonString = raw.slice(start, end + 1);

// 5. Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message + "\nRaw: " + jsonString);
}

// 6. Return parsed fields as the node's output
return [
  {
    json: parsed
  }
];

// -------------------------------------------
// 1. Extract workbook output safely
// -------------------------------------------

// The AI Agent output is usually inside one of:
// - $input.item.json.output
// - or directly $input.item.json
// and then nested like:
//   [ { output: [ { content: [ { text: "{...json...}" } ] } ] } ]
// or:
//   [ { content: [ { text: "{...json...}" } ] } ]
// or already a JSON string/object.
//
// So we defensively unwrap all forms.

let raw = $input.item.json.output ?? $input.item.json;

// If the agent wrapped it inside output.output:
if (raw && raw.output) raw = raw.output;

// If it's an array → iteratively unwrap until we reach a string or object
if (Array.isArray(raw)) {
  // Keep unwrapping layers like:
  // [ { output: [...] } ]  →  [...]  →  { content: [...] }  →  text
  while (Array.isArray(raw)) {
    const first = raw[0];
    if (!first) break;

    // Case: { output: [ { ... } ] }
    if (first.output && Array.isArray(first.output)) {
      raw = first.output;
      continue;
    }

    // Case: { content: [ { text: "..." } ] }
    if (
      first.content &&
      Array.isArray(first.content) &&
      first.content[0] &&
      typeof first.content[0].text === "string"
    ) {
      raw = first.content[0].text;
      break;
    }

    // Unknown array shape, stop unwrapping
    break;
  }
}

// If still a string → parse it as JSON
if (typeof raw === "string") {
  try {
    raw = JSON.parse(raw);
  } catch (e) {
    throw new Error("Workbook output is not valid JSON:\n" + raw);
  }
}

// At this point, raw should look like:
// {
//   user_preferences: { style, length },
//   chapters_summary: [...],
//   complete_book_summary: "...",
//   index: [...]
// }

// -------------------------------------------
// 2. Build summaries_v2 record payload
// -------------------------------------------

return {
  json: {
    style: raw.user_preferences?.style || "workbook",
    length: raw.user_preferences?.length || null,

    // The actual workbook chapter output (questions, etc.)
    chapters_summary: raw.chapters_summary || [],

    // Full-book summary
    complete_book_summary: raw.complete_book_summary || "",

    // Optional index if you want to store it
    index: raw.index || []
  }
};

