const tones = [
  "friendly and conversational",
  "concise and professional",
  "enthusiastic and curious",
  "casual and personal",
  "warm and sincere",
  "playfully bold",
  "analytical and thoughtful"
];

const structures = [
  "Start with a personal compliment, then offer value, then invite a reply.",
  "Begin with a question, then share a quick insight, then close with a short CTA.",
  "Open with context or relevance, explain briefly what you do, and end with curiosity.",
  "Start with appreciation, ask something engaging, and finish with a low-pressure invitation.",
  "Open directly with value, then show credibility, then close with humor or authenticity."
];

const ctas = [
  "Would you be open to exploring this further?",
  "Worth a quick chat?",
  "Happy to send more details if it’s relevant.",
  "No rush, but I thought it might be up your alley.",
  "If not a fit, no worries — just wanted to reach out.",
  "Open to connecting sometime this week?",
  "I’d love to hear your take on this."
];

const signatures = [
  `Best,\nTatjana Haufler`,
  `Cheers,\nT. Haufler`,
  `Thanks,\nTatjana`,
  `Warm regards,\nTatjana Haufler`,
  "- Tatjana"
];

const fakeNames = ["Lukas", "Anja", "Felix", "Paul", "Anna"];

const fakeTopics = [
  "Green Tech",
  "Sustainable Packaging",
  "Startup growth tactics",
  "AI-powered workflows",
  "Project Management",
  "marketing data cleanup",
  "client onboarding automation"
];


return [
  {
    json: {
      tone: tones[Math.floor(Math.random() * tones.length)],
      structure: structures[Math.floor(Math.random() * structures.length)],
      cta: ctas[Math.floor(Math.random() * ctas.length)],
      signature: signatures[Math.floor(Math.random() * signatures.length)],
      first_name: fakeNames[Math.floor(Math.random() * fakeNames.length)],
    topic: fakeTopics[Math.floor(Math.random() * fakeTopics.length)]

    }
  }
];

