// Get arrays of incoming items
const companies = $input.first().json.body;
//const mailboxes = 

const output = [];

// Loop through companies (assume equal length)
for (let i = 0; i < companies.length; i++) {
  const company = companies[i];
  const mailbox = $('Get Mailboxes').all()[i].json; // index-aligned record

  // --- Clean email ---
  let email = company.email;

  if (email && typeof email === "string") {
    const parts = email
      .split(/[,;]+/)
      .map(e => e.trim())
      .filter(Boolean);

    company.email = parts.length > 0 ? parts[0] : "";
  }

  // --- Add from_box ---
  company.from_box = mailbox?.address || "";

  // add to the output items
  output.push({ json: company });
}

return output;

