const data = $json;
const chapters = data.chapters_summary || [];
const bookSummary = data.complete_book_summary || "";
const bookTitle = data.book_title || "Study Workbook";
const accent = "#264653";

// Helper to clean chapter titles
function cleanTitle(title = "", index) {
  return title.replace(/^(Chapter\s*\d+[:.\-]?\s*|[0-9IVXLCDM]+\s+)/i, "").trim();
}

// Build Table of Contents
const toc = chapters
  .map(ch => `<li>${cleanTitle(ch.title)}</li>`)
  .join("");

// Build global index
const indexEntries = [];


let title = $("Merge").first().json.title;
let author = $("Merge").first().json.author;




let html = `
<html>
<head>
  <style>
    :root { --accent: ${accent}; }
    body {
      font-family: "Helvetica Neue", Arial, sans-serif;
      padding: 40px;
      color: #333;
      line-height: 1.6;
    }
    h1, h2, h3 { color: var(--accent); }
    h1 {
      text-align: center;
      border-bottom: 3px solid var(--accent);
      padding-bottom: 10px;
      margin-bottom: 30px;
    }
    h2 {
      border-bottom: 1px solid #ccc;
      padding-bottom: 4px;
      margin-top: 40px;
    }
    h3 { margin-top: 25px; }
    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 15px;
      font-size: 0.95em;
    }
    td, th {
      border: 1px solid #aaa;
      padding: 8px;
      vertical-align: top;
    }
    th { background: #f0f0f0; }
    tr:nth-child(even) td { background: #fafafa; }
    .chapter {
      page-break-before: always;
      margin-top: 60px;
    }
    .summary { margin-top: 10px; margin-bottom: 20px; }
    .cover {
      text-align: center;
      page-break-after: always;
      margin-top: 200px;
    }
    footer {
      margin-top: 50px;
      text-align: center;
      font-size: 0.8em;
      color: #777;
      border-top: 1px solid #ccc;
      padding-top: 10px;
    }
    ol { margin-left: 25px; }
    .answer-space {
      border-bottom: 1px solid #bbb;
      height: 25px;
      margin: 5px 0 15px;
    }
  </style>
</head>
<body>

  <!-- Cover Page -->
  <div class="cover">
    <h1>${title}</h1>
    <h2>${author}</h2>
    <p>Workbook Summary</p>
    <p><em>Generated by Megyk Books</em></p>
  </div>

  <!-- Table of Contents -->
  <h2>Table of Contents</h2>
  <ol>${toc}</ol>
`;

// Loop through chapters
for (const ch of chapters) {
  const questions = ch.questions || [];
  const answers = ch.answers || [];

  // Add to global index
  questions.forEach((q, i) => {
    indexEntries.push({
      chapter_index: ch.chapter_index,
      chapter_title: cleanTitle(ch.title),
      question: q,
      answer: answers[i] || ""
    });
  });

  html += `
  <div class="chapter">
    <h2>Chapter ${ch.chapter_index}: ${cleanTitle(ch.title)}</h2>
    ${ch.summary ? `<div class="summary"><p>${ch.summary}</p></div>` : ""}

    ${questions.length ? `
      <h3>Questions for Reflection</h3>
      <ol>
        ${questions.map(q => `
          <li>${q}
            <div class="answer-space"></div>
            <div class="answer-space"></div>
          </li>`).join("")}
      </ol>
    ` : ""}
  </div>`;
}

// Add complete book summary
html += `
  <div class="chapter">
    <h2>Complete Book Summary</h2>
    <p>${bookSummary}</p>
  </div>
`;

// Add global Q&A Index
if (indexEntries.length) {
  html += `
  <div class="chapter">
    <h2>Workbook Index — All Questions & Answers</h2>
    <table>
      <tr><th>Chapter</th><th>Question</th><th>Answer</th></tr>`;

  let lastChapter = "";
  for (const e of indexEntries) {
    const chapterLabel = e.chapter_title !== lastChapter
      ? `Chapter ${e.chapter_index}: ${e.chapter_title}`
      : "";
    html += `
      <tr>
        <td>${chapterLabel}</td>
        <td>${e.question}</td>
        <td>${e.answer}</td>
      </tr>`;
    lastChapter = e.chapter_title;
  }

  html += `
    </table>
  </div>`;
}


// Footer
html += `
  <footer>
    Generated on ${new Date().toLocaleDateString()} by Megyk Books
  </footer>
</body>
</html>
`;

return [{ html }];

// Get the LLM message content (handle array or object safely)
let raw = $json;
if (Array.isArray(raw)) raw = raw[0];
raw = typeof raw === 'string' ? raw : JSON.stringify(raw);

// Step 1 — Remove code fences and trim
raw = raw.replace(/^```(?:json)?/i, '').replace(/```$/, '').trim();


// Step 2 — If double-encoded, unescape once
if (raw.startsWith('"') && raw.endsWith('"')) {
  try {
    raw = JSON.parse(raw);
  } catch (e) {
    // ignore if not actually double encoded
  }
}

// Step 3 — Find the last closing bracket before trailing junk
const lastSquare = raw.lastIndexOf(']');
const lastCurly = raw.lastIndexOf('}');
const cutoff = Math.max(lastSquare, lastCurly);
if (cutoff !== -1) raw = raw.slice(0, cutoff + 1);

// Step 4 — Attempt to parse the clean portion
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (err) {
  // If it still fails, try to extract both array and trailing summary manually
  const match = raw.match(/(\[.*\])\s*,\s*({.*})/s);
  if (match) {
    try {
      const chapters = JSON.parse(match[1]);
      const summary = JSON.parse(match[2]);
      parsed = { chapters, summary };
    } catch (e2) {
      parsed = { error: e2.message, snippet: raw.slice(0, 500) };
    }
  } else {
    parsed = { error: err.message, snippet: raw.slice(0, 500) };
  }
}

return parsed;

