const data = items[0].json;
const prefs = data.user_preferences || {};
const chapters = data.chapters_summary || [];
const summary = data.complete_book_summary || "";

function toList(content) {
  if (!content) return "";
  if (Array.isArray(content)) {
    return `<ul>${content.map(b => `<li>${b.trim()}</li>`).join("\n")}</ul>`;
  }
  const sentences = content
    .split(/(?<=[.?!])\s+/)
    .map(s => s.trim())
    .filter(Boolean);
  return `<ul>${sentences.map(s => `<li>${s}</li>`).join("\n")}</ul>`;
}

function cleanTitle(ch, withNumber = true) {
  let title = ch.title || "";
  // Remove any leading chapter word, number, or colon (like "1", "Chapter 1:", etc.)
  title = title.replace(/^(Chapter\s*\d+[:.\-]?\s*|[0-9IVXLCDM]+\s+)/i, "").trim();
  return withNumber ? `Chapter ${ch.chapter_index + 1}: ${title}` : title;
}




