// Number of recipients to randomly pick (you can map this dynamically)
let n = $('Max Emails for Today').first().json['Max Emails'] || 5;
// Get the incoming array of recipients
const recipients = items.map(item => item.json);

// Shuffle the list
const shuffled = recipients.sort(() => 0.5 - Math.random());

// Pick the first n from the shuffled list
const selected = shuffled.slice(0, n);

// Return each as a separate item
return selected.map(r => ({ json: r }));

